// Format data for email
const data = items[0].json;

let subject = '';
let body = '';

if (data.serviceType === 'General Inquiry (Intro Form)') {
    subject = `New General Inquiry from ${data.contact.firstName} ${data.contact.lastName}`;
    body = `
    <h3>New Inquiry Received</h3>
    <p><strong>Name:</strong> ${data.contact.firstName} ${data.contact.lastName}</p>
    <p><strong>Email:</strong> ${data.contact.email}</p>
    <p><strong>Message:</strong> ${data.message}</p>
    `;
} else if (data.serviceType === 'AI Brainstormer Request') {
    subject = `New AI Brainstormer Request`;
    body = `
    <h3>New AI Brainstormer Request</h3>
    <p><strong>Details:</strong> ${data.message}</p>
    `;
} else {
    subject = `New Quote Request: ${data.serviceType}`;
    body = `
    <h3>New Quote Request</h3>
    <p><strong>Service:</strong> ${data.serviceType}</p>
    <h4>Contact Details</h4>
    <p><strong>Name:</strong> ${data.contact.firstName} ${data.contact.lastName}</p>
    <p><strong>Email:</strong> ${data.contact.email}</p>
    <p><strong>Phone:</strong> ${data.contact.phone}</p>
    <h4>Property Details</h4>
    <p><strong>Address:</strong> ${data.property.address}</p>
    <p><strong>Ownership:</strong> ${data.property.ownership}</p>
    <p><strong>Roof Type:</strong> ${data.property.roofType}</p>
    <h4>Preferences</h4>
    <p><strong>Installation:</strong> ${data.preferences.installTimeframe}</p>
    <p><strong>Battery:</strong> ${data.preferences.batteryTimeframe}</p>
    `;
}

return [
    {
        json: {
            subject,
            body,
            toEmail: data.contact.email,
            adminEmail: 'solidiaai@gmail.com'
        }
    }
];
